import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ContaService } from '../services/conta.service';

@Component({
  selector: 'app-conta',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './conta.component.html',
  styleUrls: ['./conta.component.css'], // Criar se necessário
})
export class ContaComponent {
  conta: any = null; // Dados da conta
  saldo: number | null = null; // Saldo da conta
  extrato: any[] = []; // Extrato da conta
  erro: string | null = null; // Mensagem de erro

  constructor(private contaService: ContaService) {}

  criarConta() {
    const novaConta = {
      nomeTitular: 'Exemplo Titular',
      saldo: 0,
      status: true,
    };

    this.contaService.criarConta(1, novaConta).subscribe(
      (response) => {
        this.conta = response;
        this.erro = null;
      },
      (error) => {
        this.erro = error.error;
      }
    );
  }

  buscarConta(idConta: number) {
    this.contaService.buscarConta(idConta).subscribe(
      (response) => {
        this.conta = response;
        this.erro = null;
      },
      (error) => {
        this.erro = error.error;
      }
    );
  }

  consultarSaldo(idConta: number | undefined) {
    if (!idConta) return;

    this.contaService.consultarSaldo(idConta).subscribe(
      (response) => {
        this.saldo = response;
        this.erro = null;
      },
      (error) => {
        this.erro = error.error;
      }
    );
  }

  realizarDeposito(idConta: number, valor: number) {
    this.contaService.realizarDeposito(idConta, valor).subscribe(
      (response) => {
        this.conta = response;
        this.erro = null;
      },
      (error) => {
        this.erro = error.error;
      }
    );
  }

  realizarSaque(idConta: number, valor: number) {
    this.contaService.realizarSaque(idConta, valor).subscribe(
      (response) => {
        this.conta = response;
        this.erro = null;
      },
      (error) => {
        this.erro = error.error;
      }
    );
  }

  obterExtrato(idConta: number | undefined) {
    if (!idConta) return;

    this.contaService.obterExtrato(idConta).subscribe(
      (response) => {
        this.extrato = response;
        this.erro = null;
      },
      (error) => {
        this.erro = error.error;
      }
    );
  }
}
