import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ContaService } from '../services/conta.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ContaDTO } from '../conta.model';

@Component({
  standalone: true,
  selector: 'app-conta',
  templateUrl: './conta-component.component.html',
  styleUrls: ['./conta-component.component.css'],
  imports: [CommonModule, FormsModule],
})
export class ContaComponent implements OnInit {
  contas: ContaDTO[] = [];
  contaSelecionada: ContaDTO | null = null; // Representa a conta ativa
  nomeTitular: string = '';
  valor: number = 0;
  mostrarExtrato: boolean = false;
  extrato: any[] = [];

  constructor(private contaService: ContaService, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.carregarContas();
  }

  carregarContas(): void {
    const idUsuario = localStorage.getItem('idUsuario');
    if (!idUsuario) {
      alert('Usuário não autenticado!');
      return;
    }

    // Chama o serviço para listar as contas do usuário
    this.contaService.listarContas([+idUsuario]).subscribe({
      next: (contas) => {
        this.contas = contas;
        if (contas.length > 0) {
          this.contaSelecionada = contas[0]; // Seleciona a primeira conta por padrão
          this.carregarDetalhesConta();
        }
      },
      error: (err) => console.error('Erro ao listar contas:', err),
    });
  }

  carregarDetalhesConta(): void {
    if (this.contaSelecionada) {
      const idConta = this.contaSelecionada.idConta;
      this.contaService.getSaldo(idConta).subscribe({
        next: (saldo) => (this.contaSelecionada!.saldo = saldo),
        error: (err) => console.error('Erro ao obter saldo:', err),
      });

      if (this.mostrarExtrato) {
        this.contaService.getExtrato(idConta).subscribe({
          next: (extrato) => (this.extrato = extrato),
          error: (err) => console.error('Erro ao obter extrato:', err),
        });
      }
    }
  }

  criarConta(): void {
    const idUsuario = localStorage.getItem('idUsuario');
    if (!idUsuario) {
      alert('Usuário não autenticado!');
      return;
    }

    if (!this.nomeTitular.trim()) {
      alert('Informe o nome do titular para criar a conta.');
      return;
    }

    this.contaService
      .criarConta({ nomeTitular: this.nomeTitular, status: 'ATIVA' }, +idUsuario)
      .subscribe({
        next: (novaConta) => {
          alert('Conta criada com sucesso!');
          this.contas.push(novaConta);
          this.contaSelecionada = novaConta;
          this.carregarDetalhesConta();
        },
        error: (err) => console.error('Erro ao criar conta:', err),
      });
  }

  realizarDeposito(): void {
    if (!this.contaSelecionada || this.valor <= 0) {
      alert('Informe um valor válido para depósito.');
      return;
    }

    this.contaService.realizarDeposito(this.contaSelecionada.idConta, this.valor).subscribe({
      next: () => {
        alert('Depósito realizado com sucesso!');
        this.carregarDetalhesConta();
      },
      error: (err) => console.error('Erro ao realizar depósito:', err),
    });
  }

  realizarSaque(): void {
    if (!this.contaSelecionada || this.valor <= 0) {
      alert('Informe um valor válido para saque.');
      return;
    }

    this.contaService.realizarSaque(this.contaSelecionada.idConta, this.valor).subscribe({
      next: () => {
        alert('Saque realizado com sucesso!');
        this.carregarDetalhesConta();
      },
      error: (err) => console.error('Erro ao realizar saque:', err),
    });
  }

  alternarExtrato(): void {
    this.mostrarExtrato = !this.mostrarExtrato;
    if (this.mostrarExtrato) {
      this.carregarDetalhesConta();
    }
  }
}
<h1>Conta Bancária</h1>

<!-- Seção da conta -->
<div *ngIf="idConta">
  <h2>Detalhes da Conta</h2>
  <p><strong>ID da Conta:</strong> {{ idConta }}</p>
  <p><strong>Saldo:</strong> {{ saldo | currency:'BRL' }}</p>

  <!-- Seção de depósito e saque -->
  <div class="transacao">
    <h3>Realizar Transação</h3>
    <input
      [(ngModel)]="valor"
      placeholder="Digite o valor"
      type="number"
      min="1"
      class="input-transacao"
    />
    <button (click)="realizarDeposito()" class="btn btn-depositar">Depositar</button>
    <button (click)="realizarSaque()" class="btn btn-sacar">Sacar</button>
  </div>

  <!-- Botão para exibir ou ocultar extrato -->
  <button (click)="mostrarExtrato = !mostrarExtrato" class="btn btn-extrato">
    {{ mostrarExtrato ? 'Ocultar Extrato' : 'Mostrar Extrato' }}
  </button>

  <!-- Extrato da conta -->
  <div *ngIf="mostrarExtrato" class="extrato">
    <h3>Extrato</h3>
    <ul>
      <li *ngFor="let item of extrato">
        <strong>{{ item.data | date:'dd/MM/yyyy HH:mm' }}:</strong> 
        {{ item.descricao }} - {{ item.valor | currency:'BRL' }}
      </li>
    </ul>
  </div>
</div>

<!-- Seção para criar conta -->
<div *ngIf="!idConta">
  <h2>Criar Conta</h2>
  <input 
    [(ngModel)]="nomeTitular" 
    placeholder="Nome do Titular" 
    class="input-nome-titular" 
  />
  <select [(ngModel)]="status" class="input-status">
    <option value="ATIVA">Ativa</option>
    <option value="INATIVA">Inativa</option>
  </select>
  <button (click)="criarConta()" class="btn btn-criar">Criar Conta</button>
</div>

<!-- Seção para listar contas -->
<div *ngIf="contas.length > 0" class="listar-contas">
  <h2>Contas Existentes</h2>
  <ul>
    <li *ngFor="let conta of contas">
      <strong>ID:</strong> {{ conta.idConta }} |
      <strong>Titular:</strong> {{ conta.nomeTitular }} |
      <strong>Saldo:</strong> {{ conta.saldo | currency:'BRL' }}
    </li>
  </ul>
</div>

<!-- Botão para listar contas -->
<div *ngIf="idConta">
  <button (click)="listarContas()" class="btn btn-listar">Listar Contas</button>
</div>
