import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class UserService {
  private idUsuario: number | null = null;

  salvarIdUsuario(resposta: any): void {
    // Verifica se a resposta contém o campo id
    if (resposta && resposta.id) {  // Alterei para 'id' ao invés de 'idUsuario'
      const idUsuario = resposta.id.toString();  // Usando 'id' aqui
      console.log('ID do usuário:', idUsuario);
      localStorage.setItem('idUsuario', idUsuario);
    } else {
      console.error('A resposta não contém id:', resposta);
    }
  }

  // Método para recuperar o id do usuário
  getIdUsuario(): number | null {
    if (this.idUsuario === null) {
      const id = localStorage.getItem('idUsuario'); // Busca no localStorage
      this.idUsuario = id ? parseInt(id, 10) : null;
    }
    return this.idUsuario;
  }

  // Verifica se o usuário está logado (id != null)
  isUsuarioLogado(): boolean {
    return this.getIdUsuario() !== null; // Garante que verifica o localStorage
  }

  // Método para limpar o ID do usuário
  limparUsuario(): void {
    this.idUsuario = null;
    localStorage.removeItem('idUsuario');
  }
}
