import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ContaService } from '../services/conta.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ContaDTO } from '../conta.model';

@Component({
  standalone: true,
  selector: 'app-conta',
  templateUrl: './conta-component.component.html',
  styleUrls: ['./conta-component.component.css'],
  imports: [CommonModule, FormsModule],
})
export class ContaComponent implements OnInit {
  idConta: number | null = null;
  nomeTitular: string = '';
  saldo: number = 0;
  extrato: any[] = [];
  contas: ContaDTO[] = [];
  valor: number = 0;

  constructor(private contaService: ContaService, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.queryParams.subscribe((params) => {
      this.idConta = params['idConta'] ? +params['idConta'] : null;
      if (this.idConta) {
        this.carregarConta();
      } else {
        alert('ID da conta não encontrado!');
      }
    });
  }

  carregarConta(): void {
    if (this.idConta) {
      this.contaService.getSaldo(this.idConta).subscribe({
        next: (saldo) => (this.saldo = saldo),
        error: (err) => console.error('Erro ao obter saldo:', err),
      });

      this.contaService.getExtrato(this.idConta).subscribe({
        next: (extrato) => (this.extrato = extrato),
        error: (err) => console.error('Erro ao obter extrato:', err),
      });
    }
  }

  realizarDeposito(): void {
    if (!this.idConta || this.valor <= 0) {
      alert('Informe um valor válido para depósito.');
      return;
    }

    this.contaService.realizarDeposito(this.idConta, this.valor).subscribe({
      next: () => {
        alert('Depósito realizado com sucesso!');
        this.carregarConta();
      },
      error: () => alert('Erro ao realizar depósito.'),
    });
  }

  realizarSaque(): void {
    if (!this.idConta || this.valor <= 0) {
      alert('Informe um valor válido para saque.');
      return;
    }

    this.contaService.realizarSaque(this.idConta, this.valor).subscribe({
      next: () => {
        alert('Saque realizado com sucesso!');
        this.carregarConta();
      },
      error: (err) => {
        alert('Erro ao realizar saque.');
        console.error(err);
      },
    });
  }
}
<h1>Conta Bancária</h1>

<!-- Exibe a conta após ser criada -->
<div *ngIf="idConta">
  <p>Saldo: {{ saldo | currency }}</p>

  <!-- Campo para valor de depósito ou saque -->
  <input [(ngModel)]="valor" placeholder="Digite o valor" type="number" />

  <!-- Botões para depósito e saque -->
  <button (click)="realizarDeposito()">Depositar</button>
  <button (click)="realizarSaque()">Sacar</button>

  <!-- Exibe o extrato -->
  <div *ngIf="mostrarExtrato">
    <h2>Extrato</h2>
    <ul>
      <li *ngFor="let item of extrato">{{ item }}</li>
    </ul>
  </div>

  <!-- Botão para listar as contas -->
  <button (click)="listarContas()">Listar Contas</button>

  <!-- Exibe as contas existentes -->
  <div *ngIf="contas.length > 0">
    <h2>Contas Existentes</h2>
    <ul>
      <li *ngFor="let conta of contas">
        Conta ID: {{ conta.idConta }}, Titular: {{ conta.nomeTitular }}, Saldo: {{ conta.saldo | currency }}
      </li>
    </ul>
  </div>
</div>

<!-- Exibe o formulário para criar conta -->
<div *ngIf="!idConta">
  <h2>Criar Conta</h2>
  <input [(ngModel)]="nomeTitular" placeholder="Nome do Titular" />
  <button (click)="criarConta()">Criar Conta</button>
</div>
