// usuario.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Usuario } from '../usuario.model'; // Certifique-se de importar o modelo correto

@Injectable({
  providedIn: 'root',
})
export class UsuarioService {
  private apiUrl = 'http://localhost:8080/api/usuarios'; // URL da API

  constructor(private http: HttpClient) {}

  // Método para criar o usuário
  criarUsuario(usuarioDTO: { email: string; senha: string }): Observable<Usuario> {
    return this.http.post<Usuario>(this.apiUrl, usuarioDTO);
  }

  // Método para buscar um usuário por e-mail
  buscarUsuarioPorEmail(email: string): Observable<Usuario> {
    return this.http.get<Usuario>(`${this.apiUrl}/${email}`);
  }
}
