import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { ContaDTO } from '../conta.model';

@Injectable({
  providedIn: 'root',
})
export class ContaService {
  private baseUrl: string = 'http://seu-endpoint/api'; // Substitua pelo URL correto

  constructor(private http: HttpClient) {}

  // Método para criar a conta
  criarConta(dadosConta: { nomeTitular: string }, idUsuario: number): Observable<ContaDTO> {
    return this.http.post<ContaDTO>(`${this.baseUrl}/usuario/${idUsuario}/conta`, dadosConta);
  }
  listarContasPorUsuario(): Observable<ContaDTO[]> {
    const idUsuario = localStorage.getItem('idUsuario');
    if (!idUsuario) {
      return throwError('Usuário não encontrado no localStorage');
    }
  
    return this.http.get<ContaDTO[]>(`${this.baseUrl}/usuario/${idUsuario}/contas`);
  }


  getSaldo(idConta: number): Observable<number> {
    return this.http.get<number>(`${this.baseUrl}/conta/${idConta}/saldo`);
  }

  realizarDeposito(idConta: number, valor: number): Observable<void> {
    return this.http.post<void>(`${this.baseUrl}/conta/${idConta}/deposito`, { valor });
  }

  realizarSaque(idConta: number, valor: number): Observable<void> {
    return this.http.post<void>(`${this.baseUrl}/conta/${idConta}/saque`, { valor });
  }

  getExtrato(idConta: number): Observable<any[]> {
    return this.http.get<any[]>(`${this.baseUrl}/conta/${idConta}/extrato`);
  }
}
