import { Component, OnInit } from '@angular/core';
import { ContaService } from '../services/conta.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ContaDTO } from '../conta.model';

@Component({
  standalone: true,
  selector: 'app-conta',
  templateUrl: './conta-component.component.html',
  styleUrls: ['./conta-component.component.css'],
  imports: [CommonModule, FormsModule],
})
export class ContaComponent implements OnInit {
  contas: ContaDTO[] = [];
  contaSelecionada: ContaDTO | null = null;
  nomeTitular: string = '';
  valor: number = 0;
  mostrarExtrato: boolean = false;
  extrato: any[] = [];

  constructor(private contaService: ContaService) {}

  ngOnInit(): void {
    this.carregarContas();
  }

  /**
   * Carrega todas as contas do usuário logado.
   */
  carregarContas(): void {
    const idUsuario = +localStorage.getItem('idUsuario')!;
    if (!idUsuario) {
      alert('Usuário não autenticado!');
      return;
    }
  
    this.contaService.listarContas(idUsuario).subscribe({
      next: (contas) => {
        this.contas = contas;
        if (contas.length > 0) {
          this.selecionarConta(contas[0]); // Se já tiver contas, seleciona a primeira
        }
      },
      error: (err) => {
        console.error('Erro ao listar contas:', err);
        alert('Não foi possível carregar as contas.');
      },
    });
  }
  

  /**
   * Seleciona uma conta e carrega os detalhes dela.
   * @param conta A conta a ser selecionada.
   */
  selecionarConta(conta: ContaDTO): void {
    this.contaSelecionada = conta;
    this.carregarDetalhesConta();
  }

  /**
   * Carrega os detalhes da conta selecionada.
   */
  carregarDetalhesConta(): void {
    if (!this.contaSelecionada) return;

    const idConta = this.contaSelecionada.idConta;
    this.contaService.getSaldo(idConta).subscribe({
      next: (saldo) => {
        this.contaSelecionada!.saldo = saldo;
      },
      error: (err) => {
        console.error('Erro ao obter saldo:', err);
        alert('Não foi possível carregar o saldo.');
      },
    });

    if (this.mostrarExtrato) {
      this.carregarExtrato();
    }
  }

  /**
   * Cria uma nova conta para o usuário.
   */
  criarConta(): void {
    const idUsuario = +localStorage.getItem('idUsuario')!;
    if (!idUsuario) {
      alert('Usuário não autenticado!');
      return;
    }

    if (!this.nomeTitular.trim()) {
      alert('Informe o nome do titular para criar a conta.');
      return;
    }

    this.contaService.criarConta({ nomeTitular: this.nomeTitular }, idUsuario).subscribe({
      next: (novaConta) => {
        alert('Conta criada com sucesso!');
        this.contas.push(novaConta);
        this.selecionarConta(novaConta);
      },
      error: (err) => {
        console.error('Erro ao criar conta:', err);
        alert('Não foi possível criar a conta.');
      },
    });
  }

  /**
   * Realiza um depósito na conta selecionada.
   */
  realizarDeposito(): void {
    if (!this.contaSelecionada || this.valor <= 0) {
      alert('Informe um valor válido para depósito.');
      return;
    }

    this.contaService.realizarDeposito(this.contaSelecionada.idConta, this.valor).subscribe({
      next: () => {
        alert('Depósito realizado com sucesso!');
        this.carregarDetalhesConta(); // Atualiza o saldo da conta
      },
      error: (err) => {
        console.error('Erro ao realizar depósito:', err);
        alert('Erro ao realizar depósito. Tente novamente mais tarde.');
      },
    });
  }

  /**
   * Realiza um saque da conta selecionada.
   */
  realizarSaque(): void {
    if (!this.contaSelecionada || this.valor <= 0) {
      alert('Informe um valor válido para saque.');
      return;
    }

    this.contaService.realizarSaque(this.contaSelecionada.idConta, this.valor).subscribe({
      next: () => {
        alert('Saque realizado com sucesso!');
        this.carregarDetalhesConta(); // Atualiza o saldo da conta
      },
      error: (err) => {
        console.error('Erro ao realizar saque:', err);
        alert('Erro ao realizar saque. Tente novamente mais tarde.');
      },
    });
  }

  /**
   * Alterna a exibição do extrato da conta selecionada.
   */
  alternarExtrato(): void {
    this.mostrarExtrato = !this.mostrarExtrato;
    if (this.mostrarExtrato) {
      this.carregarExtrato();
    }
  }

  /**
   * Carrega o extrato da conta selecionada.
   */
  carregarExtrato(): void {
    if (!this.contaSelecionada) return;

    this.contaService.getExtrato(this.contaSelecionada.idConta).subscribe({
      next: (extrato) => {
        this.extrato = extrato;
      },
      error: (err) => {
        console.error('Erro ao obter extrato:', err);
        alert('Não foi possível carregar o extrato.');
      },
    });
  }
}
<div class="conta-container">
  <h2>Bem-vindo à sua conta!</h2>

  <!-- Se não houver contas, permite ao usuário criar uma nova -->
  <div *ngIf="contas.length === 0">
    <h3>Nenhuma conta encontrada. Crie uma nova conta:</h3>
    <input [(ngModel)]="nomeTitular" placeholder="Nome do Titular" />
    <button (click)="criarConta()" [disabled]="!nomeTitular.trim()">Criar Conta</button>
  </div>

  <!-- Exibe os detalhes da conta selecionada, caso existam -->
  <div *ngIf="contas.length > 0 && contaSelecionada">
    <h3>Detalhes da Conta</h3>
    <p><strong>Nome do Titular:</strong> {{ contaSelecionada.nomeTitular }}</p>
    <p><strong>Saldo:</strong> R$ {{ contaSelecionada.saldo | number:'1.2-2' }}</p>

    <!-- Formulário para realizar depósito ou saque -->
    <div class="form-deposito-saque">
      <input [(ngModel)]="valor" placeholder="Digite um valor" type="number" min="0" />
      <button (click)="realizarDeposito()" [disabled]="valor <= 0">Depositar</button>
      <button (click)="realizarSaque()" [disabled]="valor <= 0">Sacar</button>
    </div>

    <!-- Botão para alternar a exibição do extrato -->
    <button (click)="alternarExtrato()">
      {{ mostrarExtrato ? 'Ocultar Extrato' : 'Exibir Extrato' }}
    </button>

    <!-- Exibe o extrato da conta, se solicitado -->
    <div *ngIf="mostrarExtrato">
      <h4>Extrato</h4>
      <ul>
        <li *ngFor="let item of extrato">
          {{ item.descricao }} - R$ {{ item.valor | number:'1.2-2' }} em {{ item.data | date:'dd/MM/yyyy' }}
        </li>
      </ul>
    </div>
  </div>

  <!-- Caso o usuário não tenha selecionado uma conta, exibe mensagem -->
  <div *ngIf="contas.length > 0 && !contaSelecionada">
    <h3>Selecione uma conta para visualizar os detalhes.</h3>
    <ul>
      <li *ngFor="let conta of contas" (click)="selecionarConta(conta)">
        {{ conta.nomeTitular }}
      </li>
    </ul>
  </div>
</div>
