import { Component, OnInit } from '@angular/core';
import { ContaService } from '../services/conta.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  standalone: true,
  selector: 'app-conta',
  templateUrl: './conta-component.component.html',
  styleUrls: ['./conta-component.component.css'],
  imports: [CommonModule, FormsModule], // Adicionado aqui
})
export class ContaComponent implements OnInit {
  idConta: number | null = null;
  nomeTitular: string = '';
  saldo: number = 0;
  extrato: any[] = [];
  valor: number = 0;  // Variável para armazenar o valor do depósito ou saque
  mostrarExtrato: boolean = false;

  constructor(private contaService: ContaService) {}

  ngOnInit(): void {
    if (this.idConta) {
      this.atualizarSaldo();
      this.atualizarExtrato();
    }
  }

  criarConta(): void {
    if (this.nomeTitular.trim() === '') {
      alert('Preencha o nome do titular para criar uma conta.');
      return;
    }

    const contaDTO = {
      nomeTitular: this.nomeTitular,
      status: 'true',
    };

    const idUsuario = 1; // Substitua pelo ID real ou obtenha dinamicamente

    this.contaService.criarConta(contaDTO, idUsuario).subscribe({
      next: (conta: any) => {
        this.idConta = conta.idConta;
        this.saldo = conta.saldo;
        alert('Conta criada com sucesso!');
        this.atualizarExtrato();
      },
      error: (err) => {
        console.error('Erro ao criar conta', err);
        alert('Erro ao criar conta. Tente novamente.');
      },
    });
  }

  // Método para realizar o depósito
  realizarDeposito(): void {
    if (!this.idConta || this.valor <= 0) {
      alert('Informe um valor válido para depósito.');
      return;
    }
  
    this.contaService.realizarDeposito(this.idConta, this.valor).subscribe({
      next: () => {
        alert('Depósito realizado com sucesso!');
        this.atualizarSaldo();
        this.atualizarExtrato();
        this.valor = 0; // Limpar o campo de valor após o depósito
      },
      error: () => alert('Erro ao realizar depósito. Tente novamente.')
    });
  }

  // Método para realizar o saque
  realizarSaque(): void {
    if (!this.idConta || this.valor <= 0) {
      alert('Informe um valor válido para saque.');
      return;
    }

    this.contaService.realizarSaque(this.idConta, this.valor).subscribe({
      next: () => {
        alert('Saque realizado com sucesso!');
        this.atualizarSaldo();
        this.atualizarExtrato();
        this.valor = 0;  // Limpar o campo de valor após o saque
      },
      error: (err) => {
        alert('Erro ao realizar saque. Tente novamente.');
        console.error(err);
      }
    });
  }

  // Método para atualizar o saldo
  private atualizarSaldo(): void {
    if (this.idConta) {
      this.contaService.getSaldo(this.idConta).subscribe({
        next: (saldo) => (this.saldo = saldo),
        error: (err) => console.error('Erro ao obter saldo:', err),
      });
    }
  }

  // Método para atualizar o extrato
  private atualizarExtrato(): void {
    if (this.idConta) {
      this.contaService.getExtrato(this.idConta).subscribe({
        next: (extrato) => (this.extrato = extrato),
        error: (err) => console.error('Erro ao obter extrato:', err),
      });
    }
  }
}
<h1>Conta Bancária</h1>

<!-- Exibe a conta após ser criada -->
<div *ngIf="idConta">
  <p>Saldo: {{ saldo | currency }}</p>
  
  <!-- Campo para valor de depósito -->
  <input [(ngModel)]="valor" placeholder="Valor do Depósito" type="number" />
  <button (click)="realizarDeposito()">Depositar</button>

  <!-- Campo para valor de saque -->
  <input [(ngModel)]="valor" placeholder="Valor do Saque" type="number" />
  <button (click)="realizarSaque()">Sacar</button>

  <!-- Exibe o extrato -->
  <div *ngIf="mostrarExtrato">
    <h2>Extrato</h2>
    <ul>
      <li *ngFor="let item of extrato">{{ item }}</li>
    </ul>
  </div>
</div>

<!-- Exibe o formulário para criar conta -->
<div *ngIf="!idConta">
  <h2>Criar Conta</h2>
  <input [(ngModel)]="nomeTitular" placeholder="Nome do Titular" />
  <button (click)="criarConta()">Criar Conta</button>
</div>
