import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class UsuarioService {
  private apiUrl = 'http://localhost:8080/api/usuarios'; // URL da API

  constructor(private http: HttpClient) {}

  criarUsuario(usuarioDTO: { email: string; senha: string }): Observable<any> {
    return this.http.post(`${this.apiUrl}`, usuarioDTO);
  }

  buscarUsuarioPorEmail(email: string): Observable<any> {
    return this.http.get(`${this.apiUrl}/${email}`);
  }
}
