import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { forkJoin, Observable, of } from 'rxjs';
import { ContaDTO } from '../conta.model';
import { UserService } from './user.service'; // Importa o UserService

@Injectable({
  providedIn: 'root',
})
export class ContaService {
  private apiUrl = 'http://localhost:8080/api/contas'; // URL base da API

  constructor(private http: HttpClient, private userService: UserService) {}

  // Método para criar conta, utilizando o id do usuário armazenado
  criarConta(contaDTO: { nomeTitular: string; status: string }): Observable<ContaDTO> {
    const idUsuario = this.userService.getIdUsuario();
    if (idUsuario === null) {
      throw new Error('Usuário não está logado');
    }
    return this.http.post<ContaDTO>(`${this.apiUrl}/${idUsuario}`, contaDTO);
  }

// Método para listar todas as contas de um usuário
listarContas(idsContas: number[]): Observable<ContaDTO[]> {
  if (idsContas.length === 0) {
    return of([]); // Retorna um Observable com um array vazio
  }

  // Realiza múltiplas requisições HTTP para cada ID de conta
  const requisicoes = idsContas.map((idConta) =>
    this.http.get<ContaDTO>(`${this.apiUrl}/conta/${idConta}`)
  );

  // Combina os resultados das requisições em um único array
  return forkJoin(requisicoes);
}

  

  realizarDeposito(idConta: number, valor: number): Observable<any> {
    // Ajustar para enviar o valor como query parameter
    const url = `${this.apiUrl}/${idConta}/deposito?valor=${valor}`;
    return this.http.post(url, {}); // Corpo vazio
  }

  realizarSaque(idConta: number, valor: number): Observable<any> {
    // Ajustar para enviar o valor como query parameter
    const url = `${this.apiUrl}/${idConta}/saque?valor=${valor}`;
    return this.http.post(url, {}); // Corpo vazio
  }

  getSaldo(idConta: number): Observable<number> {
    return this.http.get<number>(`${this.apiUrl}/${idConta}/saldo`);
  }

  getExtrato(idConta: number): Observable<any[]> {
    return this.http.get<any[]>(`${this.apiUrl}/${idConta}/extrato`);
  }
}
