import { Component, OnInit } from '@angular/core';
import { ContaService } from '../services/conta.service';
import { UserService } from '../services/user.service'; // Importando o UserService
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ContaDTO } from '../conta.model';

@Component({
  standalone: true,
  selector: 'app-conta',
  templateUrl: './conta-component.component.html',
  styleUrls: ['./conta-component.component.css'],
  imports: [CommonModule, FormsModule], // Adicionado aqui
})
export class ContaComponent implements OnInit {
  idConta: number | null = null;
  nomeTitular: string = '';
  saldo: number = 0;
  extrato: any[] = [];
  valor: number = 0; // Variável para armazenar o valor do depósito ou saque
  mostrarExtrato: boolean = false;
  contas: any[] = []; // Variável para armazenar a lista de contas

  constructor(
    private contaService: ContaService,
    private userService: UserService // Injetando o UserService
  ) {}

  ngOnInit(): void {
    if (this.userService.isUsuarioLogado()) {
      this.listarContas(); // Lista as contas do usuário
    } else {
      alert('Usuário não está logado!');
    }
  }

  criarConta(): void {
    if (this.nomeTitular.trim() === '') {
      alert('Preencha o nome do titular para criar uma conta.');
      return;
    }

    const contaDTO = {
      nomeTitular: this.nomeTitular,
      status: 'true',
    };

    const idUsuario = this.userService.getIdUsuario(); // Obtém o id do usuário

    if (idUsuario === null) {
      alert('Usuário não está logado');
      return;
    }

    this.contaService.criarConta(contaDTO).subscribe({
      next: (conta: any) => {
        this.idConta = conta.idConta;
        this.saldo = conta.saldo;
        alert('Conta criada com sucesso!');
        this.atualizarExtrato();
      },
      error: (err) => {
        console.error('Erro ao criar conta', err);
        alert('Erro ao criar conta. Tente novamente.');
      },
    });
  }

// Método para listar as contas do usuário
listarContas(): void {
  const idsContas = this.getIdsContas(); // Método para obter os IDs das contas

  if (!idsContas || idsContas.length === 0) {
    alert('Nenhuma conta encontrada.');
    return;
  }

  this.contaService.listarContas(idsContas).subscribe({
    next: (contas: ContaDTO[]) => {
      this.contas = contas ?? []; // Garante que `contas` seja um array
      if (this.contas.length === 0) {
        alert('Nenhuma conta encontrada.');
      }
    },
    error: (err) => {
      console.error('Erro ao listar contas', err);
      alert('Erro ao listar contas. Tente novamente.');
    },
  });
}

// Método para obter os IDs das contas do usuário
private getIdsContas(): number[] {
  // Simulação de IDs de contas - substituir pela lógica adequada
  return [1, 2, 3]; // IDs fictícios. Substituir com lógica dinâmica ou API.
}


  // Método para realizar o depósito
  realizarDeposito(): void {
    if (!this.idConta || this.valor <= 0) {
      alert('Informe um valor válido para depósito.');
      return;
    }

    this.contaService.realizarDeposito(this.idConta, this.valor).subscribe({
      next: () => {
        alert('Depósito realizado com sucesso!');
        this.atualizarSaldo();
        this.atualizarExtrato();
        this.valor = 0; // Limpar o campo de valor após o depósito
      },
      error: () => alert('Erro ao realizar depósito. Tente novamente.')
    });
  }

  // Método para realizar o saque
  realizarSaque(): void {
    if (!this.idConta || this.valor <= 0) {
      alert('Informe um valor válido para saque.');
      return;
    }

    this.contaService.realizarSaque(this.idConta, this.valor).subscribe({
      next: () => {
        alert('Saque realizado com sucesso!');
        this.atualizarSaldo();
        this.atualizarExtrato();
        this.valor = 0;  // Limpar o campo de valor após o saque
      },
      error: (err) => {
        alert('Erro ao realizar saque. Tente novamente.');
        console.error(err);
      }
    });
  }

  // Método para atualizar o saldo
  private atualizarSaldo(): void {
    if (this.idConta) {
      this.contaService.getSaldo(this.idConta).subscribe({
        next: (saldo) => (this.saldo = saldo),
        error: (err) => console.error('Erro ao obter saldo:', err),
      });
    }
  }

  // Método para atualizar o extrato
  private atualizarExtrato(): void {
    if (this.idConta) {
      this.contaService.getExtrato(this.idConta).subscribe({
        next: (extrato) => (this.extrato = extrato),
        error: (err) => console.error('Erro ao obter extrato:', err),
      });
    }
  }
}
<h1>Conta Bancária</h1>

<!-- Exibe a conta após ser criada -->
<div *ngIf="idConta">
  <p>Saldo: {{ saldo | currency }}</p>

  <!-- Campo para valor de depósito ou saque -->
  <input [(ngModel)]="valor" placeholder="Digite o valor" type="number" />

  <!-- Botões para depósito e saque -->
  <button (click)="realizarDeposito()">Depositar</button>
  <button (click)="realizarSaque()">Sacar</button>

  <!-- Exibe o extrato -->
  <div *ngIf="mostrarExtrato">
    <h2>Extrato</h2>
    <ul>
      <li *ngFor="let item of extrato">{{ item }}</li>
    </ul>
  </div>

  <!-- Botão para listar as contas -->
  <button (click)="listarContas()">Listar Contas</button>

  <!-- Exibe as contas existentes -->
  <div *ngIf="contas.length === 0">
    <p>Nenhuma conta encontrada.</p>
  </div>
  
  <div *ngIf="contas.length > 0">
    <ul>
      <li *ngFor="let conta of contas">
        <strong>Conta:</strong> {{ conta.idConta }} <br>
        <strong>Nome do Titular:</strong> {{ conta.nomeTitular }} <br>
        <strong>Saldo:</strong> {{ conta.saldo | currency }} <br>
        <strong>Status:</strong> {{ conta.status }}
      </li>
    </ul>
  </div>

<!-- Exibe o formulário para criar conta -->
<div *ngIf="!idConta">
  <h2>Criar Conta</h2>
  <input [(ngModel)]="nomeTitular" placeholder="Nome do Titular" />
  <button (click)="criarConta()">Criar Conta</button>
</div>
