import { Component } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { UsuarioService } from '../services/usuario.service';
import { Usuario } from '../usuario.model'; // Importando a interface

@Component({
  standalone: true,
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  imports: [FormsModule],
})
export class HomeComponent {
  email: string = '';
  senha: string = '';

  constructor(
    private usuarioService: UsuarioService,
    private router: Router
  ) {}

  // Método para cadastrar usuário
  cadastrar(): void {
    if (!this.email || !this.senha) {
      alert('Preencha todos os campos!');
      return;
    }

    const usuarioDTO = {
      email: this.email,
      senha: this.senha,
    };

    this.usuarioService.criarUsuario(usuarioDTO).subscribe({
      next: (usuario: Usuario) => {  // Usando a interface 'Usuario'
        localStorage.setItem('idUsuario', usuario.id.toString()); // Armazenando o id do usuário no localStorage
        alert('Cadastro realizado com sucesso!');
        this.router.navigate(['/conta']);
      },
      error: (err) => {
        console.error('Erro ao cadastrar usuário', err);
        alert('Erro ao realizar o cadastro. Tente novamente.');
      },
    });
  }

  // Método para realizar login
  login(): void {
    if (!this.email || !this.senha) {
      alert('Preencha todos os campos!');
      return;
    }
  
    this.usuarioService.buscarUsuarioPorEmail(this.email).subscribe({
      next: (usuario: Usuario) => {
        // Validando senha
        if (usuario.senha === this.senha) {
          localStorage.setItem('idUsuario', usuario.id.toString()); // Armazenando o id do usuário no localStorage
          alert('Login realizado com sucesso!');
          this.router.navigate(['/conta']);
        } else {
          alert('Senha incorreta.');
        }
      },
      error: (err) => {
        console.error('Erro ao buscar usuário', err);
        alert('Usuário não encontrado.');
      },
    });
  }
}
<h1>Bem-vindo ao Banco</h1>
<form (ngSubmit)="cadastrar()">
  <label for="email">Email:</label>
  <input id="email" [(ngModel)]="email" name="email" type="email" required />

  <label for="senha">Senha:</label>
  <input id="senha" [(ngModel)]="senha" name="senha" type="password" required />

  <button type="button" (click)="login()">Login</button>
  <button type="submit">Cadastrar</button>
</form>
<h1>Bem-vindo ao Banco</h1>
<a routerLink="/conta">Ir para Conta</a>
