import { Component, OnInit } from '@angular/core';
import { ContaService } from '../services/conta.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ContaDTO } from '../conta.model';
import { TransacaoDTO } from '../transacao.model';

@Component({
  standalone: true,
  selector: 'app-conta',
  templateUrl: './conta-component.component.html',
  styleUrls: ['./conta-component.component.css'],
  imports: [CommonModule, FormsModule],
})
export class ContaComponent implements OnInit {
  contas: ContaDTO[] = [];
  contaSelecionada: ContaDTO | null = null;
  nomeTitular: string = '';
  valor: number = 0;
  mostrarExtrato: boolean = false;
  extrato: TransacaoDTO[] = []; // Tipagem correta para o extrato
    erro: boolean = false;

  constructor(private contaService: ContaService) {}

  ngOnInit(): void {
    this.listarContas();
  }

  listarContas(): void {
    const idUsuario = localStorage.getItem('idUsuario');
    if (!idUsuario) {
      alert('Usuário não autenticado! Faça login para listar as contas.');
      return;
    }
  
    this.contaService.listarContasPorUsuario().subscribe({
      next: (dados: ContaDTO | ContaDTO[]) => {
        console.log('Contas retornadas pelo backend:', dados);
  
        // Verifica se o retorno é um objeto único ou um array
        if (Array.isArray(dados)) {
          this.contas = dados;
        } else {
          this.contas = [dados]; // Converte o objeto único em um array
        }
  
        this.erro = false;
      },
      error: (err) => {
        console.error('Erro ao listar contas:', err);
        this.erro = true;
        alert('Crie uma conta!');
      },
    });
  }
  
  
  

  selecionarConta(conta: ContaDTO): void {
    this.contaSelecionada = conta;
    this.mostrarExtrato = false; // Ocultar extrato ao selecionar outra conta
  }

  criarConta(): void {
    const idUsuario = localStorage.getItem('idUsuario');
    if (!idUsuario) {
      alert('Usuário não autenticado!');
      return;
    }
  
    if (!this.nomeTitular.trim()) {
      alert('Informe o nome do titular para criar a conta.');
      return;
    }
  
    this.contaService.criarConta({ nomeTitular: this.nomeTitular }, +idUsuario).subscribe({
      next: (novaConta: ContaDTO) => {
        console.log('Resposta da API (novaConta):', novaConta); // Verifique o que está sendo retornado
        alert('Conta criada com sucesso!');
        if (novaConta) {
          this.contas.push(novaConta);  // Adiciona a nova conta à lista
          this.selecionarConta(novaConta);  // Seleciona a conta automaticamente
        } else {
          console.error('Erro: A resposta da API não contém uma conta válida.');
        }
      },
      error: (err) => {
        console.error('Erro ao criar conta:', err);
        alert('Erro ao criar a conta.');
      }
    });
  }
  
  
  

  realizarDeposito(): void {
    if (!this.contaSelecionada || this.valor <= 0) {
      alert('Informe um valor válido para depósito.');
      return;
    }
  
    this.contaService.realizarDeposito(this.contaSelecionada.idConta, this.valor).subscribe({
      next: (contaAtualizada) => {
        alert('Depósito realizado com sucesso!');
        this.contaSelecionada = contaAtualizada; // Atualiza o saldo da conta
        this.valor = 0; // Limpa o campo de valor
      },
      error: (err) => {
        console.error('Erro ao realizar depósito:', err);
        alert('Erro ao realizar depósito. Tente novamente.');
      },
    });
  }
  
  realizarSaque(): void {
    if (!this.contaSelecionada || this.valor <= 0) {
      alert('Informe um valor válido para saque.');
      return;
    }
  
    this.contaService.realizarSaque(this.contaSelecionada.idConta, this.valor).subscribe({
      next: (contaAtualizada) => {
        alert('Saque realizado com sucesso!');
        this.contaSelecionada = contaAtualizada; // Atualiza o saldo da conta
        this.valor = 0; // Limpa o campo de valor
      },
      error: (err) => {
        console.error('Erro ao realizar saque:', err);
        alert('Erro ao realizar saque, saldo insuficiente. Tente novamente.');
      },
    });
  }
  

  carregarExtrato(): void {
    if (!this.contaSelecionada) {
      alert('Selecione uma conta para visualizar o extrato.');
      return;
    }

    this.contaService.getExtrato(this.contaSelecionada.idConta).subscribe({
      next: (extrato) => {
        this.extrato = extrato;
      },
      error: (err) => {
        console.error('Erro ao carregar extrato:', err);
        alert('Erro ao carregar o extrato.');
      },
    });
  }

  carregarDetalhesConta(): void {
    if (!this.contaSelecionada) {
      return;
    }

    this.contaService.obterDetalhesConta(this.contaSelecionada.idConta).subscribe({
      next: (contaAtualizada) => {
        this.contaSelecionada = contaAtualizada;
      },
      error: (err) => {
        console.error('Erro ao carregar detalhes da conta:', err);
        alert('Erro ao carregar detalhes da conta.');
      },
    });
  }
}
<div class="content-wrapper">
  <div class="container">
    <!-- Título e nome do usuário -->
    <header class="header">
      <h1>Gerenciar Contas</h1>
    </header>

    <!-- Formulário para criar conta (aparece somente se não houver nenhuma conta) -->
    <section *ngIf="contas.length === 0" class="create-account-section">
      <h3>Criar Conta</h3>
      <label for="nomeTitular" class="label">Nome do Titular</label>
      <input 
        type="text" 
        id="nomeTitular" 
        [(ngModel)]="nomeTitular" 
        placeholder="Digite o nome do titular" 
        class="input-text" />
      <button (click)="criarConta()" class="btn-criar-conta">Criar Conta</button>
    </section>

    <!-- Listagem de contas -->
    <section class="accounts-section">
      <h3>Contas Disponíveis</h3>
      <ul class="accounts-list">
        <li *ngFor="let conta of contas" class="account-item">
          <button 
            (click)="selecionarConta(conta)" 
            [class.selected]="conta === contaSelecionada" 
            class="account-button">
            {{ conta.nomeTitular }}
          </button>
        </li>
      </ul>
    </section>

    <!-- Se uma conta for selecionada -->
    <section *ngIf="contaSelecionada" class="account-details-section">
      <h3>Detalhes da Conta: {{ contaSelecionada.nomeTitular }}</h3>

      <!-- Realizar Transação -->
      <div class="transaction-section">
        <h4>Realizar Transação</h4>
        <input 
          type="number" 
          [(ngModel)]="valor" 
          placeholder="Digite o valor" 
          class="input-number" />

        <!-- Botões de Depósito e Saque -->
        <button 
          (click)="realizarDeposito()" 
          *ngIf="valor > 0" 
          class="btn-deposito">
          Depositar
        </button>

        <button 
          (click)="realizarSaque()" 
          *ngIf="valor > 0" 
          class="btn-saque">
          Sacar
        </button>
      </div>

      <!-- Extrato -->
      <section class="extrato-container">
        <h4 class="extrato-title">Extrato da Conta</h4>
        <button class="btn-extrato" (click)="carregarExtrato()">Carregar Extrato</button>

        <div *ngIf="extrato.length > 0" class="extrato-list">
          <ul>
            <li *ngFor="let transacao of extrato" class="extrato-item">
              <span class="transacao-data">{{ transacao.dataTransacao | date: 'dd/MM/yyyy HH:mm' }}</span> - 
              <span class="transacao-tipo">{{ transacao.tipo }}</span> - 
              <span class="transacao-valor">{{ transacao.valor | currency }}</span>
            </li>
          </ul>
        </div>

        <div *ngIf="extrato.length === 0" class="no-transacoes">
          <p>Nenhuma transação registrada.</p>
        </div>
      </section>

      <!-- Detalhes da Conta -->
      <section class="conta-container">
        <div class="conta-detalhes">
          <p><strong>Saldo Atual: R</strong> <span class="conta-saldo">{{ contaSelecionada.saldo | currency }}</span></p>
        </div>
      </section>
    </section>

    <!-- Exibição de erros -->
    <section *ngIf="erro" class="error-section">
      <p class="error-message">Você ainda não tem nenhuma conta cadastrada!</p>
    </section>
  </div>
</div>
